function validateForm() {
  var x = document.forms["myForm"]["nama"].value;
  var y = document.forms["myForm"]["judul"].value;
  var z = document.forms["myForm"]["laporan"].value.length;
  var p = document.forms["myForm"]["aspek"].value;
  var mincar = 20;

  if (x == "") {
    alert("Nama harus diisi");
    return false;
  }
  if (y == "") {
  	alert("Judul harus diisi");
  	return false;
  }
  if (z < mincar){
  	alert("Panjang Laporan Minimal 20 Karater!");
    return false;
  }
  if (p == "Pilih Aspek Pelaporan/Komentar:") {
   alert("Aspek harus dipilih");
   return false;
  }  
}

function confirmDelete(delUrl) {
  if (confirm("Apakah yakin dihapus?")) {
   document.location = delUrl;
  }
}

